    private void SaveActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_SaveActionPerformed
        String KT = KodeTransaksi.getText();
        String KO = KodeObat.getSelectedItem().toString();
        String NO = NamaObat.getText();
        String K = Keterangan.getText();
        String H = Harga.getText();
        if ((KT.isEmpty()) | (KO.isEmpty()) | (NO.isEmpty()) | (K.isEmpty()) | (H.isEmpty())) {
            JOptionPane.showMessageDialog(null, "data tidak boleh kosong, silahkan dilengkapi");
            KodeObat.requestFocus();
        } else {
            try {
                Class.forName("com.mysql.jdbc.Driver");
                Connection kon = DriverManager.getConnection("" + "jdbc:mysql://localhost/apotek", "root", "");
                Statement stt = kon.createStatement();
                String SQL = "insert into transaksi values(" + "'" + KodeTransaksi.getText() + "',"
                        + "'" + KodeObat.getSelectedItem() + "',"
                        + "'" + NamaObat.getText() + "',"
                        + "'" + Keterangan.getText() + "',"
                        + "'" + Harga.getText() + "')";
                stt.executeUpdate(SQL);
                data[0] = KodeTransaksi.getText();
                data[1] = KodeObat.getSelectedItem().toString();
                data[2] = NamaObat.getText();
                data[3] = Keterangan.getText();
                data[4] = Harga.getText();
                tableModel.insertRow(0, data);
                stt.close();
                kon.close();
                BersihData();
                Save.setEnabled(false);
                SetEditOff();
            } catch (Exception ex) {
                System.err.println(ex.getMessage());
            }
        }
    }//GEN-LAST:event_SaveActionPerformed

    private void AddNewActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_AddNewActionPerformed
        BersihData();
        KodeTransaksi.requestFocus();
        Save.setEnabled(true);
        Delete.setEnabled(false);
        SetEditOn();
    }//GEN-LAST:event_AddNewActionPerformed

